import {useState,useEffect} from "react"
import {API_URL} from "../constants"

const useFetch = (requestData) =>{

    const {route,method,headers,body} = {...requestData,"route":`${API_URL}${requestData["route"]}`}
    console.log(route,method,headers,body)
    const [response,setResponse] = useState(null)
    const [loading,setLoading] = useState(false)
    const [error,setError] = useState(null)


    useEffect(()=>{

        const getResponse = async () =>{
            try {
                setLoading(true)
                const completeRoute = `${API_URL}${route}`;
                const options = {
                    method,
                    headers,
                    body
                }
                const executeApi = await fetch(route,options)
                const apiResponse = await executeApi.json()
                console.log(apiResponse)
                setResponse(apiResponse)
            } catch (error) {
                setError(error)
            } finally{
                setLoading(false)
            }

        }
        getResponse();
    },[requestData.route])

    return {response,loading,error};

}

export default useFetch;
