import styled,{css} from "styled-components";

export const MainConatiner = styled.div`
    // background-color:rgba(0,0,0,0.9);
    width:100%;
    height:100vh;
    display:flex;
    align-items:center;
    justify-content:center;
    font-family:sans-serif;

`;
export const InnerHolder = styled.section`
    display:flex;
    flex-direction:column;
    width:50%;
    row-gap:5px;

`;
export const FormTypeHolder = styled.div`
    display:flex;
    width:100%;
    flex-wrap:wrap;
    border:1px solid black;
    border-radius:25px;

`;
export const FormType = styled.div`
    width:50%;
    background-color:${({ active }) => active ? "green" : "white"};
    cursor:pointer;
    border:0.5px solid red;
    ${(type)=>{
        type==="left" && css `display:none`
    }}

`

export const Name = styled.p`
    text-align:center;
`;

export const FormHolder = styled.form`
    width:100%;
`;
export const FieldHolder = styled.div`
    display:flex;
    flex-wrap:wrap;
    width:100%;
    column-gap:7px;
    margin-bottom:10px;
    align-items:center;
    justify-content:center;
`;
export const Input = styled.input`
    width:${({ width }) => width};
    padding:1%;
    border-radius:25px;
    border:1px solid black;
`;
export const TextArea = styled.textarea` 
    width:${({ width }) => width};
    padding:1%;
    border-radius:25px;
    border:1px solid black;`
;

export const Button = styled.button`
    width:${({ width }) => width};
    padding:1% 8%;
    border-radius:25px;
    border:1px solid black;
`;
