export const ReducersStore =   async (state,action) =>{
    switch (action.type) {
        case "API_REQUEST":
            const API_CALL =  async () =>{
                const {route,method,headers,body} = action.requestData
                    const response =  await fetch (route,{
                        method,headers,body
                    }).then(response=>{
                        return response.json()
                    }).then((data)=>{
                        state = {...state,data}
                        return state;

                    })

                    return response;
            }
            await API_CALL();
            return state;


        case "STORE_TOKEN":
           const token = action.token
           return {...state,token}

        default:
            return state;
    }
}
