export const ReducersStore = (state, action) => {
    switch (action.type) {
        case "API_REQUEST":
            const API_CALL = async () => {
                const { route, method, headers, body } = action.requestData
                const respone = await fetch(route, {
                    method, headers, body
                })
                const data = await respone.json()
                return data


            }
            API_CALL().then(async usr=>await {...state,usr})
            console.log(state)
            return {...state}
        case "GET_VARIABLES_VALUE":
            return {...state,osama:"osama"}
        case "STORE_TOKEN":
            console.log("hello")
            const token = action.token
            return { ...state, token }

        default:
            return state;
    }
}
