export const ReducersStore =    (state,action) =>{
    switch (action.type) {
        case "API_REQUEST":
            var value = "osama"
            const API_CALL =  async () =>{
                const {route,method,headers,body} = action.requestData
                const respone = await fetch (route,{
                    method,headers,body
                })
                const result = await respone.json()
                state = {...state,result}
                return state;


            }
            const apiResult = API_CALL();
            if(typeof apiResult === "object" && typeof apiResult.then ==="function"){
                apiResult.then(result=>{
                    value = "idrees"
                    return {...state,result}

                })
            }
            console.log(value)
            return {...state}

        case "STORE_TOKEN":
            console.log("hello")
           const token = action.token
           return {...state,token}

        default:
            return state;
    }
}
