import {useRef,useContext, useState} from "react"
import {Input,FieldHolder, FormHolder, Button} from "../style"
import { API_URL } from "../../../constants"
import {StoreContext} from "../../../store/index"
import {useNavigate} from "react-router-dom"
const Login = () =>{
    const navigate = useNavigate()
    const email = useRef("")
    const password = useRef("")

    const {state,dispatch} = useContext(StoreContext)
    const [loading,setLoading] = useState(false)




    const handleLoginRequest = async (e) =>{
        e.preventDefault()
        setLoading(true)
        const loginPayload = {
            email:email.current.value,
            password:password.current.value
        }
        const requestData = {
            route:`${API_URL}/login`,
            method:"POST",
            headers:{
                "accept":"application/json",
                "content-type":"application/json"
            },
            body:JSON.stringify(loginPayload)
        }
    console.log("Before Dispacth")
       await dispatch({
            type:"API_REQUEST",
            requestData
        })
        dispatch({type:"GET_VARIABLES_VALUE"})
        console.log("After Dispactch")
        setLoading(false)


    }

    const TriggerDispatchOperation = async (token) =>{
            await dispatch({
                type:"STORE_TOKEN",
                token:"osama"
            })
            navigate("/dashboard")
    }
    if(typeof state === "object" && typeof state.then ==="function"){
        state.then(result=>{
            result && result.data.status && (
                TriggerDispatchOperation(result.data.token)
            ) 

        })
    }


    console.log(state)    
    return !loading?<FormHolder onSubmit={handleLoginRequest}>

        <FieldHolder>
                <Input type={"email"} name="email" placeholder="Email" width={"100%"} ref={email}/>
        </FieldHolder>
        <FieldHolder>
            <Input type={"password"} name="password" placeholder="Password" width={"100%"} ref={password}/>
        </FieldHolder>
        <FieldHolder>
            <Button name="button" value="Button" width={"max-content"}>Login</Button>
        </FieldHolder>
    {/* {apiResult&&console.log(apiResult)} */}

    </FormHolder>:"loading"
}
export default Login
