import {UseFetch} from "../Hooks/index"
export const ReducersStore = (state,action) =>{
    switch (action.type) {
        case "API_REQUEST":
            let result;
            (async()=>{
                //   const {response,loading,error} =   await UseFetch(action.requestData,action.triggerApi)
                //   return {...state,response,loading,error}
                const {route,method,headers,body} = action.requestData
                if(action.triggerApi){
                    const response = await fetch (route,{
                        method,headers,body
                    })
                    result = await response.json();
                    console.log(result)
                    return {...state,"done":true,result}
                }
                else{
                    return state
                }


            })()



        default:
            return state;
    }
}
