import {useState,useEffect} from "react"
import {API_URL} from "../constants"

const useFetch = (route,method="GET",headers=null,body=null) =>{

    const [response,setResponse] = useState(null)
    const [loading,setLoading] = useState(false)
    const [error,setError] = useState(null)


    useEffect(()=>{

        const getResponse = async () =>{
            try {
                setLoading(true)
                const completeRoute = `${API_URL}${route}`;
                const options = {
                    method,
                    headers,
                    body
                }
                const executeApi = await fetch(completeRoute,options)
                const apiResponse = await executeApi.json()
                console.log(apiResponse)
                setResponse(apiResponse)
            } catch (error) {
                setError(error)
            } finally{
                setLoading(false)
            }

        }
        getResponse();
    },[route,method,headers,body])

    return {response,loading,error};

}

export default useFetch;
