import {UseFetch} from "../Hooks/index"
export const ReducersStore = (state,action) =>{
    switch (action.type) {
        case "API_REQUEST":
            (async()=>{
                  const {response,loading,error} =   await UseFetch(action.payload)
                  console.log(response,loading,error)
            })()

            break;

        default:
            return state;
    }
}
