export const ReducersStore =  (state,action) =>{
    switch (action.type) {
        case "API_REQUEST":
            (async()=>{
            const {route,method,headers,body} = action.requestData
                    const response = await fetch (route,{
                        method,headers,body
                    })
                    const result = await response.json();
                    console.log({...state,result})
                    return {...state,result}

            })()
            break;
        case "TEST":
            return {...state,test:true}



        default:
            return state;
    }
}
