    const cnic = useRef("");
    const contactNo = useRef("");
    const address = useRef("");
    const email = useRef("");
    const password = useRef("");
    const confirmPassword = useRef("");

    const [passwordMatch,confirmPasswordMatch] = useState(false)
    const submitForm = (e) =>{
