export const ReducersStore = (state, action) => {
    switch (action.type) {
        case "API_REQUEST":
            const API_CALL = async () => {
                return new Promise  (async function(resolve,reject){
                    const { route, method, headers, body } = action.requestData
                    const respone = await fetch(route, {
                        method, headers, body
                    })
                        resolve(respone.json())
                })



            }
            const result = API_CALL()
            console.log(result.then(usr=>console.log(usr)))
            return {...state}
        case "GET_VARIABLES_VALUE":
            return {...state,osama:"osama"}
        case "STORE_TOKEN":
            console.log("hello")
            const token = action.token
            return { ...state, token }

        default:
            return state;
    }
}
