                        return response.json()
                    }).then((data)=>{
                        console.log("inside")
                        return data;

                    })

                    // const result = await response.json();
                    // return result
            }
            const data =  await API_CALL();
            console.log("outside",data)
            return {...state,data}
        case "TEST1":
            return {...state,test1:true}
        case "TEST2":
            return {...state,test2:true}



